order_join AS (SELECT attribution_channel AS marketing_channel, new_vs_repeat, created_at, post_giveback_orders AS orders, post_giveback_revenue AS revenue FROM credit_giveback UNION ALL SELECT CASE WHEN LOWER(survey_channel) LIKE '%radio%' THEN 'Radio am/fm' WHEN LOWER(survey_channel) LIKE '%tv%' THEN 'TV' ELSE survey_channel END AS marketing_channel, new_vs_repeat, created_at, reattributed_orders AS orders, reattributed_revenue AS revenue FROM reattributed_credit)